import React from "react";
import { Box, Grid, Typography } from "@mui/material";
import '../../css/Home/openinvestment.css'
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import { useEffect, useState } from "react";
import BG1 from '../../images/investments/image1.png';
import Logo1 from '../../images/investments/logo1.png';
import { makeStyles } from "@material-ui/core";
const data = [
    {
        id: 1, backgroundImage: '', logo: '', logoName: '', logoText: '', heading: '', subHeading: '', description: '',
        chip: [{ id: 1, name: 'health' }, { id: 2, name: 'Personal Health' }],
        raised: '',
        closesIn: '',
        invest: '',
    },
    {
        id: 1, backgroundImage: '', logo: '', logoName: '', logoText: '', heading: '', subHeading: '', description: '',
        chip: [{ id: 1, name: 'health' }, { id: 2, name: 'Personal Health' }],
        raised: '',
        closesIn: '',
        invest: '',
    },
    {
        id: 1, backgroundImage: '', logo: '', logoName: '', logoText: '', heading: '', subHeading: '', description: '',
        chip: [{ id: 1, name: 'health' }, { id: 2, name: 'Personal Health' }],
        raised: '',
        closesIn: '',
        invest: '',
    },
]


// const useStyles = makeStyles({
//   card: {
//     // maxWidth: 300,

//     backgroundColor: "red"
//   },
//   root:{
//     padding:'0 !important',
//   }
// });

export default function OpenInvestment() {
    const [gridxsFirst, setGridxsFirst] = useState(4)
    const [gridxsSecond, setgridxsSecond] = useState(6)
    const ratio = parseInt(window.innerWidth);
    // const classes = useStyles();

    useEffect(() => {

        if (ratio < 920) {
            setGridxsFirst(2)
            setgridxsSecond(12)
        }
        else if (ratio < 600) {
            setGridxsFirst(2)
            setgridxsSecond(6)
        }
    }, [])
    return (
        <>
            <Box sx={{ minHeight: '60vh', marginTop: '90px' }}>
                <Typography className="test-script-regular">Open<span className="test-script-bg"> Investment</span></Typography>
                <Typography style={{ fontSize: '18px', color: 'gray' }}>Text will be given by Arpit Subscribe to gain access to exclusive high return opportunities in growing business</Typography>
                <Grid sx={{ marginTop: '40px' }} container spacing={4}>
                    <Grid item xs={3}>
                        <Card sx={{ minWidth: '100%', padding: '0' }} >
                            <CardContent sx={{ padding: '0' }}>
                                <div style={{ position: 'relative' }}>
                                    <img src={BG1} width={300} height={192} className="background-img-card" />
                                    <div className="card-header-logo">
                                        <div className="company-logo-section">
                                            <img src={Logo1} width={102} height={34} />
                                        </div>
                                        <div className="logo-txt-script">CCD</div>
                                    </div>
                                    <div className="info-card-txt">
                                        <span className="company-name">ams</span>
                                    </div>
                                    <div className="centered-txt-card">
                                        <span className="company-name">MildCares-GynoCup</span>
                                    </div>
                                    <div className="bottom-txt-card">
                                        <span className="company-name">MildCares-GynoCup</span>
                                    </div>
                                </div>
                            </CardContent>
                        </Card>
                    </Grid>

                </Grid>
            </Box>
        </>
    )

}
